import { useState } from "react";

import Movie from "./components/Movie";

function App() {
  // const movies = useState([{ id: 1, title: 'Pengabdi Setan 2'}]);
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState("");

  const save = () => {
    setMovies((currentState) => {
      //currentState.push({ id: 2, title: title }) // ngga bisa
      // return currentState.filter()
      return [...currentState, { id: currentState.length + 1, title: title }];
    });
  };

  const edit = (id) => {
    setMovies((currentState) => {
      return currentState.map((state) => {
        if (state.id === id) {
          state.title = title; // hanya mengubah value dari key title
        }
        return state; // tetap return object
      });
    });
  };

  return (
    <div>
      <form>
        <div class="form-outline mb-4">
          <input type="text" id="form5Example1" class="form-control" />
          <label class="form-label" for="form5Example1">
            Name
          </label>
        </div>

        <div class="form-outline mb-4">
          <input type="text" id="form5Example2" class="form-control" />
          <label class="form-label" for="form5Example2">
            Email address
          </label>
        </div>

        <div class="form-check d-flex justify-content-center mb-4">
          <input
            class="form-check-input me-2"
            type="checkbox"
            value=""
            id="form5Example3"
            checked
          />
          <label class="form-check-label" for="form5Example3">
            I have read and agree to the terms
          </label>
        </div>

        <button type="submit" class="btn btn-primary btn-block mb-4">
          Subscribe
        </button>
      </form>
      <h1>Hello Wolrd</h1>
      <input
        type="text"
        placeholder="title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <button onClick={save}>Save</button>
      {movies.map((movie) => (
        <Movie key={movie.id} title={movie.title} />
      ))}
    </div>
  );
}

export default App;
