import { useState } from "react";

import Movie from "./components/Movie";

function App() {
  // const movies = useState([{ id: 1, title: 'Pengabdi Setan 2'}]);
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState("");

  const save = () => {
    setMovies((currentState) => {
      //currentState.push({ id: 2, title: title }) // ngga bisa
      // return currentState.filter()
      return [...currentState, { id: currentState.length + 1, title: title }];
    });
  };

  const edit = (id) => {
    setMovies((currentState) => {
      return currentState.map((state) => {
        if (state.id === id) {
          state.title = title; // hanya mengubah value dari key title
        }
        return state; // tetap return object
      });
    });
  };

  return (
    <div className="m-5 container tepi center">
      <form>
        <div class="form-outline mb-4">
          <input
            type="text"
            id="form1Example1"
            class="form-control"
            onChange={(e) => setTitle(e.target.value)}
          />
          <label class="form-label" for="form1Example1">
            Email address
          </label>
        </div>

        <div class="form-outline mb-4">
          <input type="password" id="form1Example2" class="form-control" />
          <label class="form-label" for="form1Example2">
            Password
          </label>
        </div>

        <div class="row mb-4">
          <div class="col d-flex justify-content-center">
            <div class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                value=""
                id="form1Example3"
                checked
              />
              <label class="form-check-label" for="form1Example3">
                {" "}
                Remember me{" "}
              </label>
            </div>
          </div>
        </div>
        <button onClick={save} type="submit" class="btn btn-primary btn-block">
          submit
        </button>
        {movies.map((movie) => (
          <Movie key={movie.id} title={movie.title} />
        ))}
      </form>
    </div>
  );
}

export default App;
