import { useState } from "react";

import Movie from "./components/Movie";

function App() {
  // const movies = useState([{ id: 1, title: 'Pengabdi Setan 2'}]);
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");

  const save = () => {
    setMovies((currentState) => {
      //currentState.push({ id: 2, title: title }) // ngga bisa
      // return currentState.filter()
      return [
        ...currentState,
        { id: currentState.length + 1, title: title, author: author },
      ];
    });
  };

  const edit = (id) => {
    setMovies((currentState) => {
      return currentState.map((state) => {
        if (state.id === id) {
          state.title = title;
          state.author = author;
          // hanya mengubah value dari key title
        }
        return state; // tetap return object
      });
    });
  };

  return (
    <div className="tepi">
      <h1 className="font">Alfan Bookstore</h1>
      <div className="form-outline mb-4">
        <input
          type="text"
          id="form5Example1"
          className="form-control"
          onChange={(e) => setTitle(e.target.value)}
        />
        <label className="form-label" for="form5Example1">
          Masukan Judul Buku
        </label>
      </div>

      <div className="form-outline mb-4">
        <input
          type="text"
          id="form5Example2"
          className="form-control"
          onChange={(e) => setAuthor(e.target.value)}
        />
        <label className="form-label" for="form5Example2">
          Email address
        </label>
      </div>
      <button
        type="submit"
        onClick={save}
        className="btn btn-primary btn-block mb-4"
      >
        Subscribe
      </button>
      {movies.map((movie) => (
        <Movie key={movie.id} title={movie.title} />
      ))}
    </div>
  );
}
export default App;
